export default function({
    store,
    redirect,
    route
}) {
console.log($store)
    if (!store.$cookies.get('access_token') &&
        route.path !== '/login' &&
        route.path !== '/registration'
    ) {
        console.log(1)
        redirect({
            path: '/login'
        })
    }


}
