import Cookie from 'js-cookie';

const SET_TOKEN = 'SET_TOKEN'
const LOGAUT = 'LOGAUT'
const PROFILE = 'PROFILE'

export default {
  namespaced: true,
  state: {
      token: Cookie.get('token') || null,
      refreshToken: Cookie.get('refreshToken') || null,
      permissions: Cookie.get('permissions') || null,
      profile: null,
      userId: Cookie.get('userId') || null
  },

  getters: {
    token: state => state.token,
    refreshToken: state => state.refreshToken,
    permissions: state => state.permissions,
    profile: state => state.profile,
    profileHeader: state => {
      if (state.profile)
      return {
        Person: state.profile.Person || null,
        PersonImage: state.profile.PersonImage || null
      }
    },
    userId: state => state.userId,
    loggedIn: state => !!state.token
  },

  actions: {
    async login({dispatch, commit}, payload) {
      let { data } = await this.$api.auth.login(payload)
      await commit('SET_TOKEN', data.data)
      await dispatch('getProfile', data.data.user.id)
      // return data
    }
  },
  mutations: {
    [SET_TOKEN](state, payload) {
      if (payload.accessToken !== undefined) {
        Cookie.set('token', payload.accessToken)
        Cookie.set('refreshToken', payload.refreshToken)
        Cookie.set('permissions', payload.user.permissions)
        Cookie.set('userId', payload.user.id)
        state.token = payload.accessToken
        state.refreshToken = payload.refreshToken
        state.permissions = payload.user.permissions
        state.userId = payload.user.id
      }
    },
    [PROFILE](state, payload) {
        state.profile = payload
    },
    [LOGAUT](state) {
      console.log('mutation')
      Cookie.remove('token')
      Cookie.remove('refreshToken')
      Cookie.remove('permissions')
      Cookie.remove('userId')
      state.token = null
      state.refreshToken = null
      state.permissions = null
      state.userId = null
    }
  },
};
